variable.value:
(url_tail) => {
    var cashboxId = pm.collectionVariables.get("cashboxId")
    var shift_url = 'https://market.testkontur.ru:443/cashboxApi/backend/v1/cashbox/' + cashboxId + '/' + url_tail
    const getRequest = {
        url: shift_url,
        method: 'GET',
        header: {
            'Content-Type': 'application/json',
            'Authorization': 'Basic ' + btoa(pm.collectionVariables.get("username") + ':' + pm.collectionVariables.get("password"))
        },
    };
    return getRequest
}
